import React, { useState, useEffect, useRef } from 'react';
import { usersAPI, messagesAPI } from '../../services/api';
import { hybridEncryptMessage, hybridDecryptMessage, decryptPrivateKeyAES } from '../../services/crypto';
import { getSocket } from '../../services/socket';
import { PaperAirplaneIcon, LockClosedIcon, PhoneIcon, EllipsisVerticalIcon, MagnifyingGlassIcon, FaceSmileIcon, PaperClipIcon } from '@heroicons/react/24/solid';

const ChatContainer = ({ currentUser, showStatus }) => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  // Cache decrypted private key for session
  const decryptedPrivateKeyRef = useRef(null);
  const passphraseRef = useRef('');

  useEffect(() => {
    usersAPI.getAllUsers().then(res => setUsers(res.data));
  }, []);

  useEffect(() => {
    if (selectedUser) {
      messagesAPI.getConversation(selectedUser.username).then(res => setMessages(res.data));
    }
  }, [selectedUser]);

  useEffect(() => {
    const socket = getSocket();
    if (!socket) return;
    socket.on('receive_message', (msg) => {
      if (msg.from === selectedUser?.username || msg.to === selectedUser?.username) {
        setMessages(prev => [...prev, msg]);
      }
    });
    return () => {
      socket.off('receive_message');
    };
  }, [selectedUser]);

  // Prompt for password and decrypt private key once per session
  const getDecryptedPrivateKey = async () => {
    if (decryptedPrivateKeyRef.current) return decryptedPrivateKeyRef.current;
    const encryptedPrivateKey = localStorage.getItem('privateKey');
    let passphrase = passphraseRef.current;
    if (!passphrase) {
      passphrase = window.prompt('Enter your password to decrypt your private key:');
      passphraseRef.current = passphrase;
    }
    try {
      const privateKeyArmored = await decryptPrivateKeyAES(encryptedPrivateKey, passphrase);
      decryptedPrivateKeyRef.current = privateKeyArmored;
      return privateKeyArmored;
    } catch (err) {
      showStatus('Failed to decrypt private key. Wrong password?', 'error');
      decryptedPrivateKeyRef.current = null;
      passphraseRef.current = '';
      throw err;
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input || !selectedUser) return;
    setLoading(true);
    try {
      const { encryptedMessage, encryptedAESKey } = await hybridEncryptMessage(input, selectedUser.publicKey);
      const messageData = {
        to: selectedUser.username,
        encryptedContent: encryptedMessage,
        encryptedAESKey,
        messageType: 'text'
      };
      await messagesAPI.sendMessage(messageData);
      // Add plaintext to local message for sender
      setMessages(prev => [
        ...prev,
        {
          from: currentUser.username,
          to: selectedUser.username,
          encryptedContent: encryptedMessage,
          encryptedAESKey,
          messageType: 'text',
          plaintext: input // <-- add this line
        }
      ]);
      getSocket().emit('send_message', {
        from: currentUser.username,
        to: selectedUser.username,
        encryptedContent: encryptedMessage,
        encryptedAESKey,
        messageType: 'text'
      });
      setInput('');
      showStatus('Message sent', 'success');
    } catch (err) {
      showStatus('Failed to send message', 'error');
    }
    setLoading(false);
  };

  // Dummy avatar generator (use initials)
  const getAvatar = (username) => (
    <div className="relative flex items-center justify-center w-10 h-10 rounded-full bg-indigo-500 text-white font-bold text-lg select-none">
      {username.slice(0, 2).toUpperCase()}
    </div>
  );

  // Dummy last message preview (could be improved if available)
  const getLastPreview = (user) => {
    const lastMsg = messages.filter(
      m => m.from === user.username || m.to === user.username
    ).slice(-1)[0];
    if (!lastMsg) return '';
    return lastMsg.plaintext || '[Encrypted]';
  };

  const renderUsers = () => (
    <ul className="space-y-1 mt-4">
      {users.map(user => (
        <li key={user.username}>
          <button
            onClick={() => setSelectedUser(user)}
            className={`w-full flex items-center gap-3 px-3 py-2 rounded-lg transition-colors duration-150 hover:bg-slate-700 focus:bg-slate-700 ${
              selectedUser?.username === user.username ? 'bg-slate-700' : ''
            }`}
          >
            <div className="relative">
              {getAvatar(user.username)}
              <span className={`absolute bottom-0 right-0 block w-3 h-3 rounded-full border-2 border-slate-800 ${user.isOnline ? 'bg-green-400' : 'bg-gray-400'}`}></span>
            </div>
            <div className="flex-1 text-left">
              <div className="font-bold text-base">{user.username}</div>
              <div className="text-xs text-gray-400 truncate">{getLastPreview(user)}</div>
            </div>
          </button>
        </li>
      ))}
    </ul>
  );

  const renderMessages = () => (
    <div className="flex flex-col-reverse gap-2 overflow-y-auto h-full px-4 py-2">
      {messages.map((msg, idx) => (
        <MessageItem
          key={idx}
          msg={msg}
          currentUser={currentUser}
          getDecryptedPrivateKey={getDecryptedPrivateKey}
        />
      ))}
    </div>
  );

  return (
    <div className="flex h-screen font-inter bg-gradient-to-br from-slate-900 via-slate-950 to-indigo-950">
      {/* Sidebar */}
      <aside className="flex flex-col w-80 min-w-[18rem] bg-gradient-to-b from-slate-800 via-slate-900 to-slate-950 text-gray-200 border-r border-slate-700 h-full shadow-xl">
        <div className="px-6 py-5 border-b border-slate-700 flex flex-col gap-4 backdrop-blur-md">
          <div className="flex items-center gap-2">
            <span className="font-extrabold text-2xl tracking-tight text-indigo-400 drop-shadow-lg">SecureChat</span>
            <LockClosedIcon className="w-6 h-6 text-indigo-400" />
          </div>
          <div className="relative">
            <input
              type="text"
              className="w-full pl-10 pr-3 py-2 rounded-lg bg-slate-700/80 text-gray-200 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition shadow"
              placeholder="Search users..."
            />
            <MagnifyingGlassIcon className="absolute left-3 top-2.5 w-5 h-5 text-gray-400" />
          </div>
        </div>
        <div className="flex-1 overflow-y-auto px-2">{renderUsers()}</div>
      </aside>

      {/* Main Chat Area */}
      <main className="flex-1 flex flex-col h-full">
        {selectedUser ? (
          <>
            {/* Header Bar */}
            <div className="flex items-center justify-between px-8 py-5 bg-slate-900/90 border-b border-slate-700 shadow-lg backdrop-blur-md">
              <div className="flex items-center gap-4">
                {getAvatar(selectedUser.username)}
                <div>
                  <div className="font-bold text-xl text-white">{selectedUser.username}</div>
                  <div className="flex items-center gap-2 text-xs text-gray-400">
                    <span className={`w-2 h-2 rounded-full ${selectedUser.isOnline ? 'bg-green-400' : 'bg-gray-400'} shadow`} />
                    {selectedUser.isOnline ? 'Online' : 'Offline'}
                  </div>
                </div>
              </div>
              <div className="flex items-center gap-4">
                <button className="p-2 rounded-full hover:bg-indigo-600/20 transition shadow">
                  <PhoneIcon className="w-5 h-5 text-indigo-400" />
                </button>
                <button className="p-2 rounded-full hover:bg-indigo-600/20 transition shadow">
                  <EllipsisVerticalIcon className="w-5 h-5 text-indigo-400" />
                </button>
              </div>
            </div>

            {/* Message List */}
            <div className="flex-1 overflow-y-auto bg-slate-900 px-0 py-0 flex flex-col-reverse">
              {renderMessages()}
            </div>

            {/* Message Composer */}
            <form
              onSubmit={sendMessage}
              className="flex items-center gap-3 px-8 py-5 bg-slate-800/90 border-t border-slate-700 shadow-lg rounded-b-xl"
              style={{ position: 'sticky', bottom: 0, zIndex: 10 }}
            >
              <button type="button" className="p-2 rounded-full hover:bg-indigo-600/20 transition shadow">
                <FaceSmileIcon className="w-6 h-6 text-indigo-400" />
              </button>
              <button type="button" className="p-2 rounded-full hover:bg-indigo-600/20 transition shadow">
                <PaperClipIcon className="w-6 h-6 text-indigo-400" />
              </button>
              <textarea
                value={input}
                onChange={e => setInput(e.target.value)}
                placeholder="Type your message..."
                required
                rows={1}
                className="flex-1 resize-none bg-slate-700/80 text-white rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition placeholder-gray-400 shadow"
                style={{ minHeight: '2.5rem', maxHeight: '8rem' }}
              />
              <button
                type="submit"
                disabled={loading || !input}
                className={`ml-2 p-2 rounded-full bg-indigo-600 hover:bg-indigo-700 transition shadow-lg flex items-center justify-center ${(!input || loading) ? 'opacity-50 cursor-not-allowed' : ''}`}
              >
                <PaperAirplaneIcon className="w-1 h-1 text-white" />
              </button>
            </form>
          </>
        ) : (
          <div className="flex flex-col items-center justify-center h-full text-gray-400">
            <span className="text-3xl font-extrabold mb-2 text-indigo-400 drop-shadow-lg">Welcome to SecureChat</span>
            <span className="text-lg">Select a user to start chatting.</span>
          </div>
        )}
      </main>
    </div>
  );
};

// MessageItem component with bubble styling
const MessageItem = ({ msg, currentUser, getDecryptedPrivateKey }) => {
  const [decrypted, setDecrypted] = useState('');

  useEffect(() => {
    const decrypt = async () => {
      if (msg.from === currentUser.username && msg.plaintext) {
        setDecrypted(msg.plaintext);
      } else {
        try {
          const privateKeyArmored = await getDecryptedPrivateKey();
          const dec = await hybridDecryptMessage(msg.encryptedContent, msg.encryptedAESKey, privateKeyArmored);
          setDecrypted(dec);
        } catch {
          setDecrypted('[Encrypted]');
        }
      }
    };
    decrypt();
  }, [msg, currentUser]);

  const isSent = msg.from === currentUser.username;
  const bubbleClasses = isSent
    ? 'bg-gradient-to-br from-indigo-600 via-indigo-700 to-indigo-800 text-white self-end rounded-2xl shadow-xl'
    : 'bg-gradient-to-br from-slate-700 via-slate-800 to-slate-900 text-white self-start rounded-2xl shadow-xl';
  const alignClasses = isSent ? 'justify-end' : 'justify-start';

  return (
    <div className={`flex ${alignClasses} items-end gap-3`}>
      <div className={`max-w-[70%] px-5 py-3 ${bubbleClasses} transition-all duration-300 border border-slate-800`}>
        <div className="flex items-center gap-2">
          <span className="break-words text-base leading-relaxed">{decrypted}</span>
        </div>
        <div className="flex items-center gap-1 mt-2 text-xs text-gray-300">
          <LockClosedIcon className="w-2 h-2 inline-block" />
          <span>{new Date(msg.timestamp || Date.now()).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
        </div>
      </div>
    </div>
  );
};

export default ChatContainer;
