import React, { useState, useEffect, useRef } from 'react';
import { usersAPI, messagesAPI } from '../../services/api';
import { hybridEncryptMessage, hybridDecryptMessage, decryptPrivateKeyAES } from '../../services/crypto';
import { getSocket } from '../../services/socket';
import { PaperAirplaneIcon, LockClosedIcon, PhoneIcon, EllipsisVerticalIcon, MagnifyingGlassIcon, FaceSmileIcon, PaperClipIcon, UserCircleIcon, Bars3Icon } from '@heroicons/react/24/solid';

const ChatContainer = ({ currentUser, showStatus }) => {
  // --- STATE FOR RESPONSIVENESS ---
  const [isDesktop, setDesktop] = useState(window.innerWidth >= 768);
  const [isSidebarOpen, setSidebarOpen] = useState(false);

  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  const decryptedPrivateKeyRef = useRef(null);
  const passphraseRef = useRef('');

  // --- EFFECT TO DETECT SCREEN SIZE ---
  useEffect(() => {
    const handleResize = () => setDesktop(window.innerWidth >= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    usersAPI.getAllUsers().then(res => setUsers(res.data));
  }, []);

  useEffect(() => {
    if (selectedUser) {
      messagesAPI.getConversation(selectedUser.username).then(res => setMessages(res.data));
      // Close sidebar on mobile when a user is selected
      if (!isDesktop) {
        setSidebarOpen(false);
      }
    }
  }, [selectedUser, isDesktop]);

  useEffect(() => {
    const socket = getSocket();
    if (!socket) return;
    socket.on('receive_message', (msg) => {
      if (msg.from === selectedUser?.username || msg.to === selectedUser?.username) {
        setMessages(prev => [...prev, msg]);
      }
    });
    return () => {
      socket.off('receive_message');
    };
  }, [selectedUser]);

  const getDecryptedPrivateKey = async () => {
    if (decryptedPrivateKeyRef.current) return decryptedPrivateKeyRef.current;
    const encryptedPrivateKey = localStorage.getItem('privateKey');
    let passphrase = passphraseRef.current;
    if (!passphrase) {
      passphrase = window.prompt('Enter your password to decrypt your private key:');
      passphraseRef.current = passphrase;
    }
    try {
      const privateKeyArmored = await decryptPrivateKeyAES(encryptedPrivateKey, passphrase);
      decryptedPrivateKeyRef.current = privateKeyArmored;
      return privateKeyArmored;
    } catch (err) {
      showStatus('Failed to decrypt private key. Wrong password?', 'error');
      decryptedPrivateKeyRef.current = null;
      passphraseRef.current = '';
      throw err;
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input || !selectedUser) return;
    setLoading(true);
    try {
      const { encryptedMessage, encryptedAESKey } = await hybridEncryptMessage(input, selectedUser.publicKey);
      const messageData = {
        to: selectedUser.username,
        encryptedContent: encryptedMessage,
        encryptedAESKey,
        messageType: 'text'
      };
      await messagesAPI.sendMessage(messageData);
      setMessages(prev => [
        ...prev,
        {
          from: currentUser.username,
          to: selectedUser.username,
          encryptedContent: encryptedMessage,
          encryptedAESKey,
          messageType: 'text',
          plaintext: input
        }
      ]);
      getSocket().emit('send_message', {
        from: currentUser.username,
        to: selectedUser.username,
        encryptedContent: encryptedMessage,
        encryptedAESKey,
        messageType: 'text'
      });
      setInput('');
      showStatus('Message sent', 'success');
    } catch (err) {
      showStatus('Failed to send message', 'error');
    }
    setLoading(false);
  };

  const getAvatar = (username) => (
    <div className="relative flex items-center justify-center w-10 h-10 rounded-full bg-slate-600 text-white font-bold text-lg select-none flex-shrink-0">
      {username.slice(0, 2).toUpperCase()}
    </div>
  );

  const getLastPreview = (user) => "Click to view messages";

  // --- DYNAMIC INLINE STYLES FOR RESPONSIVENESS ---
  const sidebarStyle = {
    position: 'absolute',
    left: 0,
    top: 0,
    height: '100%',
    width: '20rem',
    flexShrink: 0,
    zIndex: 20,
    transform: isDesktop || isSidebarOpen ? 'translateX(0)' : 'translateX(-100%)',
    transition: 'transform 0.3s ease-in-out',
  };

  if (isDesktop) {
    sidebarStyle.position = 'relative';
  }

  return (
    <div style={{ display: 'flex', height: '100vh', width: '100%', overflow: 'hidden' }} className="bg-slate-900 font-sans text-gray-300">
      <aside style={sidebarStyle} className="flex flex-col bg-slate-800 text-gray-200 border-r border-slate-700">
        <div className="px-5 py-4 border-b border-slate-700">
          <h1 className="text-xl font-bold text-gray-100">SecureChat</h1>
        </div>
        <div className="px-5 py-4">
          <div className="relative">
            <input type="text" className="w-full pl-10 pr-3 py-2 rounded-md bg-slate-900 text-gray-200 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition" placeholder="Search or start new chat" />
            <MagnifyingGlassIcon style={{ width: '1.25rem', height: '1.25rem' }} className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-500" />
          </div>
        </div>
        <div className="flex-1 overflow-y-auto px-2">
          <ul className="space-y-1 pt-2">
            {users.map(user => (
              <li key={user.username}>
                <button onClick={() => setSelectedUser(user)} className={`w-full flex items-center gap-4 px-3 py-2 rounded-lg transition-colors duration-150 hover:bg-slate-700/50 focus:outline-none ${selectedUser?.username === user.username ? 'bg-slate-700' : ''}`}>
                  <div className="relative">{getAvatar(user.username)}<span className={`absolute bottom-0 right-0 block w-3 h-3 rounded-full border-2 border-slate-900 ${user.isOnline ? 'bg-green-400' : 'bg-gray-400'}`}></span></div>
                  <div className="flex-1 text-left min-w-0">
                    <div className="font-semibold text-base text-gray-200 truncate">{user.username}</div>
                    <div className="text-sm text-gray-400 truncate">{getLastPreview(user)}</div>
                  </div>
                </button>
              </li>
            ))}
          </ul>
        </div>
      </aside>

      <main style={{ flex: 1, minWidth: 0, display: 'flex', flexDirection: 'column' }}>
        {selectedUser ? (
          <>
            <div className="flex items-center justify-between px-4 sm:px-6 py-3 bg-slate-900/80 backdrop-blur-sm border-b border-slate-700 flex-shrink-0">
              <div className="flex items-center gap-3 min-w-0">
                {!isDesktop && (
                  <button onClick={() => setSidebarOpen(true)} className="p-2 rounded-full hover:bg-slate-800 transition">
                    <Bars3Icon style={{width: '1.5rem', height: '1.5rem'}} className="text-gray-400" />
                  </button>
                )}
                {getAvatar(selectedUser.username)}
                <div className="min-w-0">
                  <div className="font-bold text-lg text-white truncate">{selectedUser.username}</div>
                  <div className="flex items-center gap-1.5 text-xs text-gray-400">
                    <span className={`w-2 h-2 rounded-full ${selectedUser.isOnline ? 'bg-green-400' : 'bg-gray-400'}`}></span>
                    {selectedUser.isOnline ? 'Online' : 'Offline'}
                  </div>
                </div>
              </div>
              <div className="flex items-center gap-2">
                <button className="p-2 rounded-full hover:bg-slate-800 transition"><PhoneIcon style={{ width: '1.25rem', height: '1.25rem' }} className="text-gray-400" /></button>
                <button className="p-2 rounded-full hover:bg-slate-800 transition"><EllipsisVerticalIcon style={{ width: '1.25rem', height: '1.25rem' }} className="text-gray-400" /></button>
              </div>
            </div>

            <div className="flex-1 overflow-y-auto flex flex-col-reverse p-4">
              {messages.map((msg, idx) => (<MessageItem key={idx} msg={msg} currentUser={currentUser} getDecryptedPrivateKey={getDecryptedPrivateKey} />))}
            </div>

            <form onSubmit={sendMessage} className="flex items-center gap-3 px-4 py-3 bg-slate-800 border-t border-slate-700">
              <button type="button" className="p-2 rounded-full hover:bg-slate-700 transition"><FaceSmileIcon style={{ width: '1.5rem', height: '1.5rem' }} className="text-gray-400" /></button>
              <button type="button" className="p-2 rounded-full hover:bg-slate-700 transition"><PaperClipIcon style={{ width: '1.5rem', height: '1.5rem' }} className="text-gray-400" /></button>
              <textarea value={input} onChange={e => setInput(e.target.value)} placeholder="Type a message..." required rows={1} className="flex-1 resize-none bg-slate-700 text-white rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition placeholder-gray-400" style={{ minHeight: '2.75rem', maxHeight: '10rem' }}/>
              <button type="submit" disabled={loading || !input} className={`p-3 rounded-full bg-indigo-600 hover:bg-indigo-700 transition shadow-lg flex items-center justify-center disabled:opacity-50 disabled:cursor-not-allowed`}>
                <PaperAirplaneIcon style={{ width: '1.5rem', height: '1.5rem' }} className="text-white" />
              </button>
            </form>
          </>
        ) : (
          <div className="flex flex-col items-center justify-center h-full text-center p-8">
            {!isDesktop && (
              <button onClick={() => setSidebarOpen(true)} className="absolute top-5 left-5 p-2 rounded-full hover:bg-slate-800 transition">
                <Bars3Icon style={{width: '1.5rem', height: '1.5rem'}} className="text-gray-400" />
              </button>
            )}
            <UserCircleIcon style={{ width: '6rem', height: '6rem' }} className="text-slate-700 mb-6"/>
            <h2 className="text-3xl font-medium text-gray-200 mb-2">Welcome to SecureChat</h2>
            <p className="text-lg text-gray-400">Select a user to begin a secure conversation.</p>
          </div>
        )}
      </main>
    </div>
  );
};

const MessageItem = ({ msg, currentUser, getDecryptedPrivateKey }) => {
  const [decrypted, setDecrypted] = useState('');

  useEffect(() => {
    const decrypt = async () => {
      if (msg.from === currentUser.username && msg.plaintext) {
        setDecrypted(msg.plaintext);
      } else {
        try {
          const privateKeyArmored = await getDecryptedPrivateKey();
          const dec = await hybridDecryptMessage(msg.encryptedContent, msg.encryptedAESKey, privateKeyArmored);
          setDecrypted(dec);
        } catch {
          setDecrypted('[Encrypted]');
        }
      }
    };
    decrypt();
  }, [msg, currentUser, getDecryptedPrivateKey]);

  const isSent = msg.from === currentUser.username;
  const bubbleClasses = isSent
    ? 'bg-indigo-600 text-white self-end rounded-xl shadow-md'
    : 'bg-slate-700 text-gray-200 self-start rounded-xl shadow';
  const alignClasses = isSent ? 'justify-end' : 'justify-start';

  return (
    <div className={`flex w-full ${alignClasses}`}>
      <div className={`max-w-xl px-4 py-2 ${bubbleClasses}`}>
        <p className="break-words">{decrypted}</p>
        <div className="text-right text-xs text-gray-300/70 mt-1">
          <span>{new Date(msg.timestamp || Date.now()).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
        </div>
      </div>
    </div>
  );
};

export default ChatContainer;
