import React, { useState, useEffect, useRef } from 'react';
import './ChatContainer.css'; // This now links to the enhanced UI

// --- PLACEHOLDER SERVICES ---
const usersAPI = {
  getAllUsers: async () => ({
    data: [
      { username: 'Alice', isOnline: true, publicKey: 'alice_pub_key' },
      { username: 'Bob', isOnline: false, publicKey: 'bob_pub_key' },
      { username: 'Charlie', isOnline: true, publicKey: 'charlie_pub_key' },
      { username: 'Diana', isOnline: false, publicKey: 'diana_pub_key' },
    ]
  })
};
const messagesAPI = {
  getConversation: async (username) => ({ data: [] }),
  sendMessage: async (messageData) => {
    console.log("Message sent to backend:", messageData);
    return { success: true };
  }
};
const getSocket = () => ({
  on: (event, callback) => console.log(`Socket listener for '${event}' is active.`),
  off: (event) => console.log(`Socket listener for '${event}' removed.`),
  emit: (event, data) => console.log(`Socket emitting '${event}':`, data),
});
const hybridEncryptMessage = async (text, publicKey) => ({
  encryptedMessage: `encrypted_for_${publicKey}(${text})`,
  encryptedAESKey: 'mock_aes_key'
});
const hybridDecryptMessage = async (encryptedContent, encryptedAESKey, privateKey) => {
    const content = encryptedContent.substring(encryptedContent.indexOf('(') + 1, encryptedContent.length - 1);
    return `decrypted: "${content}"`;
};
const decryptPrivateKeyAES = async (encryptedPrivateKey, passphrase) => {
    if (passphrase) return '---DECRYPTED_PRIVATE_KEY---';
    throw new Error('Invalid passphrase');
};


// --- SVG ICONS ---
const Icon = ({ path, className = "icon" }) => (
    <svg xmlns="http://www.w.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" className={className}>
        {path}
    </svg>
);
const PaperAirplaneIcon = ({ className }) => <Icon className={className} path={<path d="M3.478 2.405a.75.75 0 00-.926.94l2.432 7.905H13.5a.75.75 0 010 1.5H4.984l-2.432 7.905a.75.75 0 00.926.94 60.519 60.519 0 0018.445-8.986.75.75 0 000-1.218A60.517 60.517 0 003.478 2.405z" />} />;
const PhoneIcon = ({ className }) => <Icon className={className} path={<path fillRule="evenodd" d="M1.5 4.5a3 3 0 013-3h1.372c.86 0 1.61.586 1.819 1.42l1.105 4.423a1.875 1.875 0 01-.694 1.955l-1.293.97c-.135.101-.164.279-.087.431l4.287 7.427c.077.152.256.18.431.087l.97-1.293a1.875 1.875 0 011.955-.694l4.423 1.105c.834.209 1.42.959 1.42 1.82V19.5a3 3 0 01-3 3h-2.25C6.55 22.5 1.5 17.45 1.5 8.25V6h1.5V4.5z" clipRule="evenodd" />} />;
const EllipsisVerticalIcon = ({ className }) => <Icon className={className} path={<path fillRule="evenodd" d="M10.5 6a1.5 1.5 0 113 0 1.5 1.5 0 01-3 0zm0 6a1.5 1.5 0 113 0 1.5 1.5 0 01-3 0zm0 6a1.5 1.5 0 113 0 1.5 1.5 0 01-3 0z" clipRule="evenodd" />} />;
const MagnifyingGlassIcon = ({ className }) => <Icon className={className} path={<path fillRule="evenodd" d="M10.5 3.75a6.75 6.75 0 100 13.5 6.75 6.75 0 000-13.5zM2.25 10.5a8.25 8.25 0 1114.59 5.28l4.69 4.69a.75.75 0 11-1.06 1.06l-4.69-4.69A8.25 8.25 0 012.25 10.5z" clipRule="evenodd" />} />;
const FaceSmileIcon = ({ className }) => <Icon className={className} path={<path fillRule="evenodd" d="M12 2.25c-5.385 0-9.75 4.365-9.75 9.75s4.365 9.75 9.75 9.75 9.75-4.365 9.75-9.75S17.385 2.25 12 2.25zm-2.625 6c-.54 0-.975.435-.975.975s.435.975.975.975.975-.435.975-.975S9.915 8.25 9.375 8.25zm5.25 0c-.54 0-.975.435-.975.975s.435.975.975.975.975-.435.975-.975S15.165 8.25 14.625 8.25zM12 15.75a.75.75 0 01.75.75v.008c0 .414-.336.75-.75.75s-.75-.336-.75-.75v-.008a.75.75 0 01.75-.75z" clipRule="evenodd" />} />;
const PaperClipIcon = ({ className }) => <Icon className={className} path={<path fillRule="evenodd" d="M18.97 3.659a2.25 2.25 0 00-3.182 0l-10.12 10.12a2.25 2.25 0 000 3.182 2.25 2.25 0 003.182 0l7.364-7.364a.75.75 0 011.06 1.06l-7.364 7.364a3.75 3.75 0 11-5.303-5.303l10.12-10.12a3.75 3.75 0 115.303 5.303l-2.962 2.962a.75.75 0 01-1.06-1.06l2.962-2.962a2.25 2.25 0 000-3.182z" clipRule="evenodd" />} />;
const UserCircleIcon = ({ className }) => <Icon className={className} path={<path fillRule="evenodd" d="M18.685 19.097A9.723 9.723 0 0021.75 12c0-5.385-4.365-9.75-9.75-9.75S2.25 6.615 2.25 12a9.723 9.723 0 003.065 7.097A9.716 9.716 0 0012 21.75a9.716 9.716 0 006.685-2.653zm-12.54-1.285A7.486 7.486 0 0112 15a7.486 7.486 0 015.855 2.812A8.224 8.224 0 0112 20.25a8.224 8.224 0 01-5.855-2.438zM15.75 9a3.75 3.75 0 11-7.5 0 3.75 3.75 0 017.5 0z" clipRule="evenodd" />} />;
const Bars3Icon = ({ className }) => <Icon className={className} path={<path fillRule="evenodd" d="M3 6.75A.75.75 0 013.75 6h16.5a.75.75 0 010 1.5H3.75A.75.75 0 013 6.75zM3 12a.75.75 0 01.75-.75h16.5a.75.75 0 010 1.5H3.75A.75.75 0 013 12zm0 5.25a.75.75 0 01.75-.75h16.5a.75.75 0 010 1.5H3.75A.75.75 0 01-.75-.75z" clipRule="evenodd" />} />;


const MessageItem = ({ msg, currentUser, getDecryptedPrivateKey }) => {
    const [decrypted, setDecrypted] = useState('');

    useEffect(() => {
      const decrypt = async () => {
        if (msg.from === currentUser.username && msg.plaintext) {
          setDecrypted(msg.plaintext);
        } else {
          try {
            const privateKeyArmored = await getDecryptedPrivateKey();
            const dec = await hybridDecryptMessage(msg.encryptedContent, msg.encryptedAESKey, privateKeyArmored);
            setDecrypted(dec);
          } catch {
            setDecrypted('[Encrypted]');
          }
        }
      };
      decrypt();
    }, [msg, currentUser, getDecryptedPrivateKey]);

    const isSent = msg.from === currentUser.username;
    const wrapperClass = `message-item-wrapper ${isSent ? 'message-item-wrapper--sent' : 'message-item-wrapper--received'}`;
    const bubbleClass = `message-bubble ${isSent ? 'message-bubble--sent' : 'message-bubble--received'}`;

    return (
      <div className={wrapperClass}>
        <div className={bubbleClass}>
          <p>{decrypted}</p>
          <div className="message-timestamp">
            <span>{new Date(msg.timestamp || Date.now()).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
          </div>
        </div>
      </div>
    );
};

const ChatContainer = ({ currentUser, showStatus }) => {
  const [isDesktop, setDesktop] = useState(window.innerWidth >= 768);
  const [isSidebarOpen, setSidebarOpen] = useState(false);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const textareaRef = useRef(null);

  const decryptedPrivateKeyRef = useRef(null);
  const passphraseRef = useRef('');

  useEffect(() => {
    const handleResize = () => setDesktop(window.innerWidth >= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    usersAPI.getAllUsers().then(res => setUsers(res.data));
  }, []);

  useEffect(() => {
    if (selectedUser) {
      messagesAPI.getConversation(selectedUser.username).then(res => setMessages(res.data));
      if (!isDesktop) setSidebarOpen(false);
    }
  }, [selectedUser, isDesktop]);

  useEffect(() => {
    const socket = getSocket();
    if (!socket) return;
    socket.on('receive_message', (msg) => {
      if (msg.from === selectedUser?.username || msg.to === selectedUser?.username) {
        setMessages(prev => [...prev, msg]);
      }
    });
    return () => socket.off('receive_message');
  }, [selectedUser]);

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
    }
  }, [input]);

  const getDecryptedPrivateKey = async () => {
    if (decryptedPrivateKeyRef.current) return decryptedPrivateKeyRef.current;
    const encryptedPrivateKey = localStorage.getItem('privateKey');
    let passphrase = passphraseRef.current;
    if (!passphrase) {
      passphrase = window.prompt('Enter your password to decrypt your private key:');
      if (!passphrase) throw new Error("No passphrase provided.");
      passphraseRef.current = passphrase;
    }
    try {
      const privateKeyArmored = await decryptPrivateKeyAES(encryptedPrivateKey, passphrase);
      decryptedPrivateKeyRef.current = privateKeyArmored;
      return privateKeyArmored;
    } catch (err) {
      showStatus('Failed to decrypt private key. Wrong password?', 'error');
      decryptedPrivateKeyRef.current = null;
      passphraseRef.current = '';
      throw err;
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input.trim() || !selectedUser) return;
    setLoading(true);
    try {
      const { encryptedMessage, encryptedAESKey } = await hybridEncryptMessage(input, selectedUser.publicKey);
      const messageData = {
        from: currentUser.username,
        to: selectedUser.username,
        encryptedContent: encryptedMessage,
        encryptedAESKey,
        messageType: 'text',
        timestamp: Date.now(),
      };
      await messagesAPI.sendMessage(messageData);
      setMessages(prev => [...prev, { ...messageData, plaintext: input }]);
      getSocket().emit('send_message', messageData);
      setInput('');
      showStatus('Message sent', 'success');
    } catch (err) {
      showStatus('Failed to send message', 'error');
    }
    setLoading(false);
  };

  const handleEnterPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
        e.preventDefault();
        sendMessage(e);
    }
  };

  const getAvatar = (username) => (
    <div className="avatar">
      {username.slice(0, 2).toUpperCase()}
    </div>
  );

  const getLastPreview = (user) => "Click to view messages";

  const sidebarWrapperClass = `sidebar-wrapper ${isDesktop || isSidebarOpen ? 'sidebar-wrapper--open' : ''}`;

  return (
    <div className="chat-container">
      <div className={sidebarWrapperClass}>
        <aside className="sidebar">
          <div className="sidebar-header">
            <h1>SecureChat</h1>
          </div>
          <div className="sidebar-search">
            <input type="text" placeholder="Search or start new chat" />
            <MagnifyingGlassIcon className="icon" />
          </div>
          <ul className="user-list">
            {users.filter(u => u.username !== currentUser?.username).map(user => (
              <li key={user.username}>
                <button 
                    onClick={() => setSelectedUser(user)} 
                    className={`user-item ${selectedUser?.username === user.username ? 'user-item--selected' : ''}`}
                >
                  <div className="avatar">
                    {user.username.slice(0, 2).toUpperCase()}
                    <span className={`status-indicator ${user.isOnline ? 'status-indicator--online' : 'status-indicator--offline'}`}></span>
                  </div>
                  <div className="user-info">
                    <div className="user-info__name">{user.username}</div>
                    <div className="user-info__preview">{getLastPreview(user)}</div>
                  </div>
                </button>
              </li>
            ))}
          </ul>
        </aside>
      </div>

      <main className="main-content">
        {selectedUser ? (
          <>
            <div className="chat-header">
              <div className="chat-header__left">
                {!isDesktop && (
                  <button onClick={() => setSidebarOpen(true)} className="menu-button">
                    <Bars3Icon className="icon" />
                  </button>
                )}
                {getAvatar(selectedUser.username)}
                <div>
                  <div className="chat-header__name">{selectedUser.username}</div>
                  <div className="chat-header__status">
                    <span className={`chat-header__status-dot ${selectedUser.isOnline ? 'chat-header__status-dot--online' : 'chat-header__status-dot--offline'}`}></span>
                    {selectedUser.isOnline ? 'Online' : 'Offline'}
                  </div>
                </div>
              </div>
              <div className="chat-header__right">
                <button className="icon-button"><PhoneIcon className="icon" /></button>
                <button className="icon-button"><EllipsisVerticalIcon className="icon" /></button>
              </div>
            </div>

            <div className="message-list">
              {messages.map((msg, idx) => (<MessageItem key={msg.timestamp || idx} msg={msg} currentUser={currentUser} getDecryptedPrivateKey={getDecryptedPrivateKey} />))}
            </div>

            <div className="message-form-container">
              <form onSubmit={sendMessage} className="message-form">
                <button type="button" className="icon-button"><FaceSmileIcon className="icon" /></button>
                <button type="button" className="icon-button"><PaperClipIcon className="icon" /></button>
                <textarea 
                  ref={textareaRef} 
                  value={input} 
                  onChange={e => setInput(e.target.value)} 
                  onKeyDown={handleEnterPress} 
                  placeholder="Type a message..." 
                  required 
                  rows={1} 
                  className="message-form__textarea"
                />
                <button type="submit" disabled={loading || !input.trim()} className="message-form__submit-button">
                  <PaperAirplaneIcon className="icon" />
                </button>
              </form>
            </div>
          </>
        ) : (
          <div className="welcome-screen">
            {!isDesktop && (
              <button onClick={() => setSidebarOpen(true)} className="menu-button">
                <Bars3Icon className="icon" />
              </button>
            )}
            <UserCircleIcon className="welcome-screen__icon"/>
            <h2 className="welcome-screen__title">Welcome, {currentUser?.username || "User"}!</h2>
            <p className="welcome-screen__subtitle">Select a user to begin a secure conversation.</p>
          </div>
        )}
      </main>
    </div>
  );
};

export default ChatContainer;
