  const getLastPreview = (user) => {
    return "Click to view messages";
  };

  const renderUsers = () => (
    <div className="flex-1 overflow-y-auto px-2">
        <ul className="space-y-1 pt-2">
            {users.map(user => (
                <li key={user.username}>
                <button
                    onClick={() => setSelectedUser(user)}
                    className={`w-full flex items-center gap-4 px-3 py-2 rounded-lg transition-colors duration-150 hover:bg-slate-700/50 focus:outline-none ${
                    selectedUser?.username === user.username ? 'bg-slate-700' : ''
                    }`}
                >
                    <div className="relative">
                    {getAvatar(user.username)}
                    <span className={`absolute bottom-0 right-0 block w-3 h-3 rounded-full border-2 border-slate-900 ${user.isOnline ? 'bg-green-400' : 'bg-gray-400'}`}></span>
                    </div>
                    <div className="flex-1 text-left min-w-0">
                      <div className="font-semibold text-base text-gray-200 truncate">{user.username}</div>
                      <div className="text-sm text-gray-400 truncate">{getLastPreview(user)}</div>
                    </div>
