    <div className="relative w-full h-screen flex overflow-hidden bg-slate-900 font-sans text-gray-300">
      {/* Sidebar for contacts */}
      <aside className="absolute md:relative z-20 flex flex-col w-80 min-w-[20rem] flex-shrink-0 bg-slate-800 text-gray-200 border-r border-slate-700 h-full transform -translate-x-full md:translate-x-0 transition-transform duration-300 ease-in-out">
