import React, { useState, useEffect, useRef } from 'react';
import { usersAPI, messagesAPI } from '../../services/api';
import { hybridEncryptMessage, hybridDecryptMessage, decryptPrivateKeyAES } from '../../services/crypto';
import { getSocket } from '../../services/socket';
// I've added UserCircleIcon for the welcome screen
import { PaperAirplaneIcon, LockClosedIcon, PhoneIcon, EllipsisVerticalIcon, MagnifyingGlassIcon, FaceSmileIcon, PaperClipIcon, UserCircleIcon } from '@heroicons/react/24/solid';

const ChatContainer = ({ currentUser, showStatus }) => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  // Cache decrypted private key for session
  const decryptedPrivateKeyRef = useRef(null);
  const passphraseRef = useRef('');

  useEffect(() => {
    usersAPI.getAllUsers().then(res => setUsers(res.data));
  }, []);

  useEffect(() => {
    if (selectedUser) {
      messagesAPI.getConversation(selectedUser.username).then(res => setMessages(res.data));
    }
  }, [selectedUser]);

  useEffect(() => {
    const socket = getSocket();
    if (!socket) return;
    socket.on('receive_message', (msg) => {
      if (msg.from === selectedUser?.username || msg.to === selectedUser?.username) {
        setMessages(prev => [...prev, msg]);
      }
    });
    return () => {
      socket.off('receive_message');
    };
  }, [selectedUser]);

  // Prompt for password and decrypt private key once per session
  const getDecryptedPrivateKey = async () => {
    if (decryptedPrivateKeyRef.current) return decryptedPrivateKeyRef.current;
    const encryptedPrivateKey = localStorage.getItem('privateKey');
    let passphrase = passphraseRef.current;
    if (!passphrase) {
      passphrase = window.prompt('Enter your password to decrypt your private key:');
      passphraseRef.current = passphrase;
    }
    try {
      const privateKeyArmored = await decryptPrivateKeyAES(encryptedPrivateKey, passphrase);
      decryptedPrivateKeyRef.current = privateKeyArmored;
      return privateKeyArmored;
    } catch (err) {
      showStatus('Failed to decrypt private key. Wrong password?', 'error');
      decryptedPrivateKeyRef.current = null;
      passphraseRef.current = '';
      throw err;
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input || !selectedUser) return;
    setLoading(true);
    try {
      const { encryptedMessage, encryptedAESKey } = await hybridEncryptMessage(input, selectedUser.publicKey);
      const messageData = {
        to: selectedUser.username,
        encryptedContent: encryptedMessage,
        encryptedAESKey,
        messageType: 'text'
      };
      await messagesAPI.sendMessage(messageData);
      // Add plaintext to local message for sender
      setMessages(prev => [
        ...prev,
        {
          from: currentUser.username,
          to: selectedUser.username,
          encryptedContent: encryptedMessage,
          encryptedAESKey,
          messageType: 'text',
          plaintext: input // <-- add this line
        }
      ]);
      getSocket().emit('send_message', {
        from: currentUser.username,
        to: selectedUser.username,
        encryptedContent: encryptedMessage,
        encryptedAESKey,
        messageType: 'text'
      });
      setInput('');
      showStatus('Message sent', 'success');
    } catch (err) {
      showStatus('Failed to send message', 'error');
    }
    setLoading(false);
  };

  const getAvatar = (username) => (
    <div className="relative flex items-center justify-center w-10 h-10 rounded-full bg-slate-600 text-white font-bold text-lg select-none">
      {username.slice(0, 2).toUpperCase()}
    </div>
  );

  // Changed last message preview to static text as per the image
  const getLastPreview = (user) => {
    return "Click to view messages";
  };

  const renderUsers = () => (
    <div className="flex-1 overflow-y-auto px-2">
        <ul className="space-y-1 pt-2">
            {users.map(user => (
                <li key={user.username}>
                <button
                    onClick={() => setSelectedUser(user)}
                    className={`w-full flex items-center gap-4 px-3 py-2 rounded-lg transition-colors duration-150 hover:bg-slate-700/50 focus:outline-none ${
                    selectedUser?.username === user.username ? 'bg-slate-700' : ''
                    }`}
                >
                    <div className="relative">
                    {getAvatar(user.username)}
                    <span className={`absolute bottom-0 right-0 block w-3 h-3 rounded-full border-2 border-slate-800 ${user.isOnline ? 'bg-green-400' : 'bg-gray-400'}`}></span>
