      {/* Main Chat Area */}
      <main className="relative flex-1 flex flex-col h-full bg-slate-900">
        {selectedUser ? (
          <>
            {/* Header Bar */}
            <div className="flex items-center justify-between px-6 py-4 bg-slate-900/80 backdrop-blur-sm border-b border-slate-700">
              <div className="flex items-center gap-3">
                {getAvatar(selectedUser.username)}
                <div>
                  <div className="font-bold text-lg text-white">{selectedUser.username}</div>
                  <div className="flex items-center gap-1.5 text-xs text-gray-400">
                    <span className={`w-2 h-2 rounded-full ${selectedUser.isOnline ? 'bg-green-400' : 'bg-gray-400'}`}></span>
                    {selectedUser.isOnline ? 'Online' : 'Offline'}
                  </div>
                </div>
              </div>
              <div className="flex items-center gap-2">
                <button className="p-2 rounded-full hover:bg-slate-800 transition">
                  <PhoneIcon className="w-5 h-5 text-gray-400" />
                </button>
                <button className="p-2 rounded-full hover:bg-slate-800 transition">
                  <EllipsisVerticalIcon className="w-5 h-5 text-gray-400" />
                </button>
              </div>
            </div>

            {/* Message List */}
            <div className="flex-1 overflow-y-auto flex flex-col-reverse p-4">
              {renderMessages()}
            </div>

            {/* Message Composer */}
            <form
              onSubmit={sendMessage}
              className="flex items-center gap-3 px-4 py-3 bg-slate-800 border-t border-slate-700"
            >
              <button type="button" className="p-2 rounded-full hover:bg-slate-700 transition">
                <FaceSmileIcon className="w-6 h-6 text-gray-400" />
              </button>
              <button type="button" className="p-2 rounded-full hover:bg-slate-700 transition">
                <PaperClipIcon className="w-6 h-6 text-gray-400" />
              </button>
              <textarea
                value={input}
                onChange={e => setInput(e.target.value)}
                placeholder="Type a message..."
                required
                rows={1}
                className="flex-1 resize-none bg-slate-700 text-white rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition placeholder-gray-400"
                style={{ minHeight: '2.75rem', maxHeight: '10rem' }}
              />
              <button
                type="submit"
                disabled={loading || !input}
                className={`p-3 rounded-full bg-indigo-600 hover:bg-indigo-700 transition shadow-lg flex items-center justify-center disabled:opacity-50 disabled:cursor-not-allowed`}
              >
                <PaperAirplaneIcon className="w-6 h-6 text-white" />
              </button>
            </form>
          </>
        ) : (
          // Updated welcome screen to match the image
          <div className="flex flex-col items-center justify-center h-full text-center text-gray-400 p-8">
            <UserCircleIcon className="w-24 h-24 text-slate-700 mb-6"/>
            <h2 className="text-3xl font-medium text-gray-200 mb-2">Welcome to SecureChat</h2>
            <p className="text-lg text-gray-400">Your conversations are end-to-end encrypted.</p>
            <p className="mt-1 text-gray-500">Select a user from the sidebar to begin a secure conversation.</p>
            {/* Added floating button from the image */}
            <div className="absolute bottom-5 right-5">
              <button className="p-2 rounded-full bg-slate-700 hover:bg-slate-600 transition">
                <EllipsisVerticalIcon className="w-6 h-6 text-gray-400"/>
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

// MessageItem component (no changes made here)
const MessageItem = ({ msg, currentUser, getDecryptedPrivateKey }) => {
  const [decrypted, setDecrypted] = useState('');

  useEffect(() => {
    const decrypt = async () => {
      if (msg.from === currentUser.username && msg.plaintext) {
        setDecrypted(msg.plaintext);
      } else {
        try {
          const privateKeyArmored = await getDecryptedPrivateKey();
          const dec = await hybridDecryptMessage(msg.encryptedContent, msg.encryptedAESKey, privateKeyArmored);
          setDecrypted(dec);
        } catch {
          setDecrypted('[Encrypted]');
        }
      }
    };
    decrypt();
  }, [msg, currentUser, getDecryptedPrivateKey]);

  const isSent = msg.from === currentUser.username;
  const bubbleClasses = isSent
    ? 'bg-indigo-600 text-white self-end rounded-xl shadow-md'
    : 'bg-slate-700 text-gray-200 self-start rounded-xl shadow';
  const alignClasses = isSent ? 'justify-end' : 'justify-start';

  return (
    <div className={`flex w-full ${alignClasses}`}>
      <div className={`max-w-xl px-4 py-2 ${bubbleClasses}`}>
        <p className="break-words">{decrypted}</p>
        <div className="text-right text-xs text-gray-300/70 mt-1">
          <span>{new Date(msg.timestamp || Date.now()).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
        </div>
      </div>
    </div>
  );
};

