import React, { useState, useEffect, useRef } from 'react';
import { usersAPI, messagesAPI } from '../../services/api';
import { hybridEncryptMessage, hybridDecryptMessage, decryptPrivateKeyAES } from '../../services/crypto';
import { getSocket } from '../../services/socket';

const ChatContainer = ({ currentUser, showStatus }) => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  // Cache decrypted private key for session
  const decryptedPrivateKeyRef = useRef(null);
  const passphraseRef = useRef('');

  useEffect(() => {
    usersAPI.getAllUsers().then(res => setUsers(res.data));
  }, []);

  useEffect(() => {
    if (selectedUser) {
      messagesAPI.getConversation(selectedUser.username).then(res => setMessages(res.data));
    }
  }, [selectedUser]);

  useEffect(() => {
    const socket = getSocket();
    if (!socket) return;
    socket.on('receive_message', (msg) => {
      if (msg.from === selectedUser?.username || msg.to === selectedUser?.username) {
        setMessages(prev => [...prev, msg]);
      }
    });
    return () => {
      socket.off('receive_message');
    };
  }, [selectedUser]);

  // Prompt for password and decrypt private key once per session
  const getDecryptedPrivateKey = async () => {
    if (decryptedPrivateKeyRef.current) return decryptedPrivateKeyRef.current;
    const encryptedPrivateKey = localStorage.getItem('privateKey');
    let passphrase = passphraseRef.current;
    if (!passphrase) {
      passphrase = window.prompt('Enter your password to decrypt your private key:');
      passphraseRef.current = passphrase;
    }
    try {
      const privateKeyArmored = await decryptPrivateKeyAES(encryptedPrivateKey, passphrase);
      decryptedPrivateKeyRef.current = privateKeyArmored;
      return privateKeyArmored;
    } catch (err) {
      showStatus('Failed to decrypt private key. Wrong password?', 'error');
      decryptedPrivateKeyRef.current = null;
      passphraseRef.current = '';
      throw err;
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input || !selectedUser) return;
    setLoading(true);
    try {
      const { encryptedMessage, encryptedAESKey } = await hybridEncryptMessage(input, selectedUser.publicKey);
      const messageData = {
        to: selectedUser.username,
        encryptedContent: encryptedMessage,
        encryptedAESKey,
        messageType: 'text'
      };
      await messagesAPI.sendMessage(messageData);
      setMessages(prev => [...prev, { from: currentUser.username, to: selectedUser.username, encryptedContent: encryptedMessage, encryptedAESKey, messageType: 'text' }]);
      getSocket().emit('send_message', { from: currentUser.username, to: selectedUser.username, encryptedContent: encryptedMessage, encryptedAESKey, messageType: 'text' });
      setInput('');
      showStatus('Message sent', 'success');
    } catch (err) {
      showStatus('Failed to send message', 'error');
    }
    setLoading(false);
  };

  const renderMessages = () => messages.map((msg, idx) => (
    <MessageItem key={idx} msg={msg} currentUser={currentUser} getDecryptedPrivateKey={getDecryptedPrivateKey} />
  ));

  return (
    <div className="chat-container">
      <aside className="user-list">
        <h3>Users</h3>
        <ul>
          {users.map(user => (
            <li key={user.username}>
              <button onClick={() => setSelectedUser(user)}>
                {user.username} {user.isOnline ? 'ðŸŸ¢' : 'âšª'}
              </button>
            </li>
          ))}
        </ul>
      </aside>
      <main className="chat-main">
        {selectedUser ? (
          <>
            <h3>Chat with {selectedUser.username}</h3>
            <div className="messages">{renderMessages()}</div>
            <form onSubmit={sendMessage} className="send-form">
              <input
                type="text"
                value={input}
                onChange={e => setInput(e.target.value)}
                placeholder="Type your message..."
                required
              />
              <button type="submit" disabled={loading}>Send</button>
            </form>
          </>
        ) : (
          <div>Select a user to start chatting.</div>
        )}
      </main>
    </div>
  );
};

const MessageItem = ({ msg, currentUser, getDecryptedPrivateKey }) => {
  const [decrypted, setDecrypted] = useState('');
  useEffect(() => {
    const decrypt = async () => {
      try {
        if (msg.from === currentUser.username) {
          setDecrypted('[You] ' + msg.encryptedContent);
        } else {
          const privateKey = await getDecryptedPrivateKey();
          const passphrase = window.prompt ? '' : ''; // passphrase is handled in getDecryptedPrivateKey
          const dec = await hybridDecryptMessage(msg.encryptedContent, msg.encryptedAESKey, privateKey, passphrase);
          setDecrypted(dec);
        }
      } catch {
        setDecrypted('[Encrypted]');
      }
    };
    decrypt();
    // eslint-disable-next-line
  }, [msg, currentUser]);
  return <div className={`message ${msg.from === currentUser.username ? 'sent' : 'received'}`}>{decrypted}</div>;
};

export default ChatContainer;
