import React, { useState, useEffect, useRef } from 'react';

// --- MOCK SERVICES ---
// NOTE: To resolve the import errors, these are placeholder implementations
// for the services that would exist in your app. Your original logic remains intact.
const usersAPI = {
  getAllUsers: async () => {
    console.log("Mock usersAPI.getAllUsers called");
    return { data: [
        { username: 'Alice', isOnline: true, publicKey: 'key_alice' },
        { username: 'Bob', isOnline: false, publicKey: 'key_bob' },
    ] };
  }
};

const messagesAPI = {
  getConversation: async (username) => {
    console.log(`Mock messagesAPI.getConversation called for ${username}`);
    return { data: [] };
  },
  sendMessage: async (messageData) => {
    console.log("Mock messagesAPI.sendMessage called with:", messageData);
    return Promise.resolve();
  }
};

const getSocket = () => ({
  on: (event, callback) => console.log(`Socket listener attached for: ${event}`),
  off: (event) => console.log(`Socket listener detached for: ${event}`),
  emit: (event, data) => console.log(`Socket emitting ${event}:`, data),
});

const hybridEncryptMessage = async (text, key) => {
    console.log("Mock hybridEncryptMessage called");
    return {
        encryptedMessage: `encrypted(${text})`,
        encryptedAESKey: 'mock_aes_key'
    };
};

const hybridDecryptMessage = async (encrypted, aesKey, privateKey) => {
    console.log("Mock hybridDecryptMessage called");
    if (typeof encrypted === 'string' && encrypted.startsWith('encrypted(')) {
        return encrypted.substring(10, encrypted.length - 1);
    }
    return '[Decrypted Message]';
};

const decryptPrivateKeyAES = async (encryptedKey, passphrase) => {
    console.log('Mock decryptPrivateKeyAES called');
    if (passphrase) {
        return '---BEGIN PGP PRIVATE KEY BLOCK---...';
    }
    throw new Error("Mock decryption failed");
};


// --- STYLES ---
// The component's styles have been updated to match the provided app.css file.
// Additional styles have been added for elements not covered in the original CSS
// to ensure a complete and polished UI, while respecting the light-theme design.
const Style = () => (
  <style>{`
    /* Base styles from your app.css */
    .chat-container-wrapper {
      font-family: Arial, sans-serif;
      background: #fff;
      border-radius: 8px;
      box-shadow: 0 2px 8px rgba(0,0,0,0.1);
      border: 1px solid #eee;
      display: flex;
      height: 80vh;
      width: 100%;
      max-width: 900px;
      margin: 0 auto;
      overflow: hidden;
    }

    .user-list {
      width: 250px;
      border-right: 1px solid #eee;
      display: flex;
      flex-direction: column;
      background-color: #fcfcfc;
    }

    .user-list-header {
      padding: 15px;
      font-size: 1.2rem;
      font-weight: bold;
      border-bottom: 1px solid #eee;
      color: #333;
    }

    .user-list-body {
      overflow-y: auto;
      flex-grow: 1;
    }

    .user-item {
      display: flex;
      align-items: center;
      padding: 12px 15px;
      cursor: pointer;
      border-bottom: 1px solid #f0f0f0;
      transition: background-color 0.2s;
    }

    .user-item:hover {
      background-color: #f1f1f1;
    }

    .user-item.selected {
      background-color: #1976d2;
      color: white;
    }
    .user-item.selected .user-name,
    .user-item.selected .user-preview {
      color: white;
    }

    .avatar {
      width: 40px;
      height: 40px;
      border-radius: 50%;
      background-color: #1976d2;
      color: white;
      display: flex;
      align-items: center;
      justify-content: center;
      margin-right: 12px;
      font-weight: bold;
    }

    .user-info {
      overflow: hidden;
    }

    .user-name {
      font-weight: bold;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }

    .user-preview {
      font-size: 0.85em;
      color: #888;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }

    .chat-main {
      flex-grow: 1;
      display: flex;
      flex-direction: column;
    }

    .chat-header {
      display: flex;
      align-items: center;
      padding: 10px 20px;
      border-bottom: 1px solid #eee;
      background: #fdfdfd;
    }

    .chat-header .user-name {
      font-size: 1.1em;
    }

    .messages {
      flex-grow: 1;
      overflow-y: auto;
      background: #fafafa;
      padding: 20px;
      display: flex;
      flex-direction: column-reverse; /* To keep view at the bottom */
    }

    .message-list-inner {
      display: flex;
      flex-direction: column;
      gap: 12px;
    }

    .message {
      display: flex;
      max-width: 75%;
    }

    .message-bubble {
      padding: 10px 15px;
