const MessageItem = ({ msg, currentUser, getDecryptedPrivateKey }) => {
    const [decrypted, setDecrypted] = useState('');

    useEffect(() => {
        const decrypt = async () => {
            if (msg.from === currentUser.username && msg.plaintext) {
                setDecrypted(msg.plaintext);
            } else {
                try {
                    const privateKeyArmored = await getDecryptedPrivateKey();
                    const dec = await hybridDecryptMessage(msg.encryptedContent, msg.encryptedAESKey, privateKeyArmored);
                    setDecrypted(dec);
                } catch {
                    setDecrypted('[Failed to decrypt]');
                }
            }
        };
        decrypt();
    }, [msg, currentUser, getDecryptedPrivateKey]);

    const isSent = msg.from === currentUser.username;
    const messageClasses = isSent ? 'message sent' : 'message received';

    return (
        <div className={messageClasses}>
            <div className="message-bubble">
                <p>{decrypted}</p>
                <div className="message-timestamp">
                    {new Date(msg.timestamp || Date.now()).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                </div>
            </div>
        </div>
    );
};

const ChatContainer = ({ currentUser, showStatus }) => {
    const [isSidebarOpen, setSidebarOpen] = useState(false);
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);

    const decryptedPrivateKeyRef = useRef(null);
    const passphraseRef = useRef('');

    useEffect(() => {
        usersAPI.getAllUsers().then(res => setUsers(res.data));
    }, []);

    useEffect(() => {
        if (selectedUser) {
            messagesAPI.getConversation(selectedUser.username).then(res => setMessages(res.data.slice().reverse()));
            setSidebarOpen(false); // Close sidebar on selection (for mobile)
        }
    }, [selectedUser]);

    const getDecryptedPrivateKey = async () => {
        if (decryptedPrivateKeyRef.current) return decryptedPrivateKeyRef.current;
        const passphrase = window.prompt('Enter your password to decrypt your private key:');
        if (!passphrase) throw new Error("Operation cancelled.");
        passphraseRef.current = passphrase;

        try {
            // Logic restored to use localStorage as per original code
            const encryptedPrivateKey = localStorage.getItem('privateKey');
            const privateKeyArmored = await decryptPrivateKeyAES(encryptedPrivateKey, passphrase);
            decryptedPrivateKeyRef.current = privateKeyArmored;
            return privateKeyArmored;
        } catch (err) {
            showStatus('Failed to decrypt private key. Wrong password?', 'error');
            passphraseRef.current = '';
            throw err;
        }
    };

    const sendMessage = async (e) => {
        e.preventDefault();
        const trimmedInput = input.trim();
        if (!trimmedInput || !selectedUser) return;
        setLoading(true);

        try {
            const { encryptedMessage, encryptedAESKey } = await hybridEncryptMessage(trimmedInput, selectedUser.publicKey);
            const messageData = {
                from: currentUser.username,
                to: selectedUser.username,
                encryptedContent: encryptedMessage,
                encryptedAESKey,
                messageType: 'text',
                timestamp: Date.now(),
                plaintext: trimmedInput 
            };
            await messagesAPI.sendMessage(messageData);
            setMessages(prev => [messageData, ...prev]);
            getSocket().emit('send_message', messageData);
            setInput('');
        } catch (err) {
            showStatus('Failed to send message. Please try again.', 'error');
        }
        setLoading(false);
    };

    const getAvatar = (username) => (
        <div className="avatar">
            {username.slice(0, 2).toUpperCase()}
        </div>
    );

    const getLastPreview = (user) => "Click to view messages";

    return (
        <>
            <Style />
            <div className="chat-container-wrapper">
                <aside className={`user-list ${isSidebarOpen ? 'open' : ''}`}>
                    <div className="user-list-header">Chats</div>
                    <div className="user-list-body">
                        {users.filter(user => user.username !== currentUser?.username).map(user => (
                            <div key={user.username} onClick={() => setSelectedUser(user)} className={`user-item ${selectedUser?.username === user.username ? 'selected' : ''}`}>
                                {getAvatar(user.username)}
                                <div className="user-info">
                                    <div className="user-name">{user.username}</div>
                                    <div className="user-preview">{getLastPreview(user)}</div>
                                </div>
                            </div>
                        ))}
                    </div>
                </aside>

                <main className="chat-main">
                    {selectedUser ? (
                        <>
                            <div className="chat-header">
                                <div className="menu-toggle" onClick={() => setSidebarOpen(!isSidebarOpen)}>☰</div>
                                {getAvatar(selectedUser.username)}
                                <div className="user-info">
                                     <div className="user-name">{selectedUser.username}</div>
                                </div>
                            </div>
                            <div className="messages">
                                <div className="message-list-inner">
                                    {messages.map((msg, idx) => (
                                        <MessageItem 
                                            key={msg.timestamp || idx}
                                            msg={msg} 
                                            currentUser={currentUser} 
                                            getDecryptedPrivateKey={getDecryptedPrivateKey} 
                                        />
                                    ))}
                                </div>
                            </div>
                            <div className="send-form-wrapper">
                                <form onSubmit={sendMessage} className="send-form">
                                    <input
                                        type="text"
                                        value={input}
                                        onChange={e => setInput(e.target.value)}
                                        placeholder="Type a message..."
                                        autoComplete="off"
                                    />
                                    <button type="submit" disabled={loading || !input.trim()}>
                                        Send
                                    </button>
                                </form>
                            </div>
                        </>
                    ) : (
                        <div className="welcome-screen">
                             <div className="menu-toggle" onClick={() => setSidebarOpen(!isSidebarOpen)} style={{position: 'absolute', top: '20px', left: '20px'}}>☰</div>
                            <h2>Welcome, {currentUser?.username || 'Guest'}</h2>
                            <p>Select a chat to start messaging.</p>
                        </div>
                    )}
                </main>
            </div>
        </>
    );
};

export default ChatContainer;
