import React, { useState, useEffect, useRef } from 'react';
import { usersAPI, messagesAPI } from '../../services/api';
import { hybridEncryptMessage, hybridDecryptMessage, decryptPrivateKeyAES } from '../../services/crypto';
import { getSocket } from '../../services/socket';
import {
  PaperAirplaneIcon,
  LockClosedIcon,
  PhoneIcon,
  EllipsisVerticalIcon,
  MagnifyingGlassIcon,
  FaceSmileIcon,
  PaperClipIcon,
  ArrowLeftIcon,
  CameraIcon,
  MicrophoneIcon,
  CheckIcon
} from '@heroicons/react/24/solid';

const WA_GREEN = "#25D366";
const messagesEndRef = React.createRef();
const textareaRef = React.createRef();

function autoGrowTextarea(e) {
  e.target.style.height = "auto";
  e.target.style.height = (e.target.scrollHeight) + "px";
}

const ChatContainer = ({ currentUser, showStatus }) => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  // Cache decrypted private key for session
  const decryptedPrivateKeyRef = useRef(null);
  const passphraseRef = useRef('');

  useEffect(() => {
    usersAPI.getAllUsers().then(res => setUsers(res.data));
  }, []);

  useEffect(() => {
    if (selectedUser) {
      messagesAPI.getConversation(selectedUser.username).then(res => setMessages(res.data));
    }
  }, [selectedUser]);

  useEffect(() => {
    const socket = getSocket();
    if (!socket) return;
    socket.on('receive_message', (msg) => {
      if (msg.from === selectedUser?.username || msg.to === selectedUser?.username) {
        setMessages(prev => [...prev, msg]);
      }
    });
    return () => {
      socket.off('receive_message');
    };
  }, [selectedUser]);

  // Prompt for password and decrypt private key once per session
  const getDecryptedPrivateKey = async () => {
    if (decryptedPrivateKeyRef.current) return decryptedPrivateKeyRef.current;
    const encryptedPrivateKey = localStorage.getItem('privateKey');
    let passphrase = passphraseRef.current;
    if (!passphrase) {
      passphrase = window.prompt('Enter your password to decrypt your private key:');
      passphraseRef.current = passphrase;
    }
    try {
      const privateKeyArmored = await decryptPrivateKeyAES(encryptedPrivateKey, passphrase);
      decryptedPrivateKeyRef.current = privateKeyArmored;
      return privateKeyArmored;
    } catch (err) {
      showStatus('Failed to decrypt private key. Wrong password?', 'error');
      decryptedPrivateKeyRef.current = null;
      passphraseRef.current = '';
      throw err;
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!input || !selectedUser) return;
    setLoading(true);
    try {
      const { encryptedMessage, encryptedAESKey } = await hybridEncryptMessage(input, selectedUser.publicKey);
      const messageData = {
        to: selectedUser.username,
        encryptedContent: encryptedMessage,
        encryptedAESKey,
        messageType: 'text'
      };
      await messagesAPI.sendMessage(messageData);
      // Add plaintext to local message for sender
      setMessages(prev => [
        ...prev,
        {
          from: currentUser.username,
          to: selectedUser.username,
          encryptedContent: encryptedMessage,
          encryptedAESKey,
          messageType: 'text',
          plaintext: input // <-- add this line
        }
      ]);
      getSocket().emit('send_message', {
        from: currentUser.username,
        to: selectedUser.username,
        encryptedContent: encryptedMessage,
        encryptedAESKey,
        messageType: 'text'
      });
      setInput('');
      showStatus('Message sent', 'success');
    } catch (err) {
      showStatus('Failed to send message', 'error');
    }
    setLoading(false);
  };

  // Dummy avatar generator (use initials)
  const getAvatar = (username, size = 1) => (
    <div className={`relative flex items-center justify-center w-${size} h-${size} rounded-full bg-indigo-500 text-white font-bold text-lg select-none`}>
      {username.slice(0, 2).toUpperCase()}
    </div>
  );

  // Dummy last message preview (could be improved if available)
  const getLastPreview = (user) => {
    const lastMsg = messages.filter(
      m => m.from === user.username || m.to === user.username
    ).slice(-1)[0];
    if (!lastMsg) return '';
    return lastMsg.plaintext || '[Encrypted]';
  };

  const renderUsers = () => (
    <ul className="space-y-1 mt-4">
      {users.map(user => (
        <li key={user.username}>
          <button
            onClick={() => setSelectedUser(user)}
            className={`w-full flex items-center gap-3 px-3 py-2 rounded-lg transition-colors duration-150 hover:bg-slate-700 focus:bg-slate-700 ${
              selectedUser?.username === user.username ? 'bg-slate-700' : ''
            }`}
          >
            <div className="relative">
              {getAvatar(user.username)}
              <span className={`absolute bottom-0 right-0 block w-3 h-3 rounded-full border-2 border-slate-800 ${user.isOnline ? 'bg-green-400' : 'bg-gray-400'}`}></span>
            </div>
            <div className="flex-1 text-left">
              <div className="font-bold text-base">{user.username}</div>
              <div className="text-xs text-gray-400 truncate">{getLastPreview(user)}</div>
            </div>
          </button>
        </li>
      ))}
    </ul>
  );



  return (
    <div className="flex h-screen font-inter text-gray-900 bg-[#ece5dd]">
      {/* Left sidebar - WhatsApp-like */}
      <aside className="flex flex-col w-80 min-w-[20rem] bg-[#111b21] text-gray-200 border-r border-slate-800 shadow-lg">
        <div className="flex items-center justify-between px-4 py-3 border-b border-slate-800">
          <div className="flex items-center gap-3">
            {getAvatar(currentUser.username, 2.3)}
            <div className="text-sm">
              <div className="font-semibold text-white">{currentUser.username}</div>
              <div className="text-xs text-gray-400">Tap to change status</div>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <button className="p-2 rounded-full hover:bg-slate-800/30"><MagnifyingGlassIcon style={{ width: 18, height: 18 }} className="text-gray-400" /></button>
            <button className="p-2 rounded-full hover:bg-slate-800/30"><EllipsisVerticalIcon style={{ width: 18, height: 18 }} className="text-gray-400" /></button>
          </div>
        </div>

        <div className="px-4 py-3">
          <div className="relative">
            <MagnifyingGlassIcon style={{ width: 18, height: 18 }} className="absolute left-3 top-3 text-gray-400" />
            <input className="w-full pl-10 pr-3 py-2 rounded-full bg-[#202c33] text-gray-200 placeholder-gray-400 focus:outline-none" placeholder="Search or start new chat" />
          </div>
        </div>

        <div className="flex-1 overflow-auto">
          {renderUsers()}
        </div>
      </aside>

      {/* Main chat area */}
      <main className="flex-1 flex flex-col bg-[url('https://i.imgur.com/6Qb6h9R.png')] bg-repeat bg-[#ece5dd] relative">
        {selectedUser ? (
          <>
            <div className="flex items-center justify-between px-4 py-3 bg-[#0b1416] border-b border-slate-800 shadow">
              <div className="flex items-center gap-3">
                <button onClick={() => setSelectedUser(null)} className="p-2 rounded-full hover:bg-slate-800/20 md:hidden"><ArrowLeftIcon style={{ width: 18, height: 18 }} className="text-gray-300" /></button>
                {getAvatar(selectedUser.username, 2.2)}
                <div className="flex flex-col">
                  <div className="font-semibold text-white">{selectedUser.username}</div>
                  <div className="text-xs text-gray-400">{selectedUser.isOnline ? 'online' : 'last seen recently'}</div>
                </div>
              </div>
              <div className="flex items-center gap-2">
                <button className="p-2 rounded-full hover:bg-slate-800/20"><PhoneIcon style={{ width: 18, height: 18 }} className="text-gray-300" /></button>
                <button className="p-2 rounded-full hover:bg-slate-800/20"><CameraIcon style={{ width: 18, height: 18 }} className="text-gray-300" /></button>
                <button className="p-2 rounded-full hover:bg-slate-800/20"><EllipsisVerticalIcon style={{ width: 18, height: 18 }} className="text-gray-300" /></button>
              </div>
            </div>

            {/* Chat bubbles */}
            <div className="flex-1 overflow-y-auto px-6 py-6">
              <div className="max-w-3xl mx-auto flex flex-col gap-3">
                {messages.map((msg, idx) => (
                  <WAChatBubble key={idx} msg={msg} currentUser={currentUser} getDecryptedPrivateKey={getDecryptedPrivateKey} />
                ))}
                <div ref={messagesEndRef} />
              </div>
            </div>

            {/* Composer like WhatsApp */}
            <form onSubmit={sendMessage} className="px-4 py-3 bg-[#111b21] border-t border-slate-800">
              <div className="max-w-3xl mx-auto flex items-center gap-3">
                <button type="button" className="p-2 rounded-full hover:bg-slate-800/20"><FaceSmileIcon style={{ width: 20, height: 20 }} className="text-gray-200" /></button>
                <button type="button" className="p-2 rounded-full hover:bg-slate-800/20"><PaperClipIcon style={{ width: 20, height: 20 }} className="text-gray-200" /></button>
                <textarea
                  ref={textareaRef}
                  value={input}
                  onChange={e => setInput(e.target.value)}
                  onInput={autoGrowTextarea}
                  placeholder={`Message ${selectedUser.username}`}
                  rows={1}
                  className="flex-1 resize-none rounded-full px-4 py-2 bg-white/95 text-black focus:outline-none"
                />
                {input.trim().length > 0 ? (
                  <button type="submit" disabled={loading} className="p-3 rounded-full" style={{ background: WA_GREEN }}>
                    <PaperAirplaneIcon style={{ width: 18, height: 18 }} className="text-white rotate-90" />
                  </button>
                ) : (
                  <button type="button" className="p-3 rounded-full bg-[#1f2a2b] hover:bg-[#2b3737]">
                    <MicrophoneIcon style={{ width: 18, height: 18 }} className="text-white" />
                  </button>
                )}
              </div>
            </form>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center">
            <div className="text-center text-gray-700">
              <h2 className="text-2xl font-semibold mb-2">Keep your phone connected</h2>
              <p className="text-sm">WhatsApp connects to your phone to sync messages. To send and receive messages, keep your phone connected.</p>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

// WhatsApp-like chat bubble component
const WAChatBubble = ({ msg, currentUser, getDecryptedPrivateKey }) => {
  const [decrypted, setDecrypted] = useState('');
  const [isDecrypting, setIsDecrypting] = useState(false);

  useEffect(() => {
    let mounted = true;
    const decrypt = async () => {
      if (msg.from === currentUser.username && msg.plaintext) {
        if (mounted) setDecrypted(msg.plaintext);
        return;
      }
      setIsDecrypting(true);
      try {
        const privateKeyArmored = await getDecryptedPrivateKey();
        const dec = await hybridDecryptMessage(msg.encryptedContent, msg.encryptedAESKey, privateKeyArmored);
        if (mounted) setDecrypted(dec);
      } catch (err) {
        if (mounted) setDecrypted('[Encrypted]');
      } finally {
        if (mounted) setIsDecrypting(false);
      }
    };
    decrypt();
    return () => { mounted = false; };
  }, [msg, currentUser]);

  const isSent = msg.from === currentUser.username;
  const timeLabel = new Date(msg.timestamp || Date.now()).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

  // WhatsApp colours: incoming = white, outgoing = green
  if (isSent) {
    return (
      <div className="self-end flex items-end gap-2 max-w-full">
        <div className="flex flex-col items-end">
          <div className="rounded-bl-2xl rounded-tl-2xl rounded-tr-xl px-4 py-2 shadow-md" style={{ background: WA_GREEN, color: 'white' }}>
            <div className="text-sm leading-relaxed">{isDecrypting ? 'Decrypting...' : decrypted}</div>
          </div>
          <div className="text-xs text-gray-200 mt-1 flex items-center gap-1 justify-end">
            <span>{timeLabel}</span>
            <CheckIcon style={{ width: 16, height: 16 }} className="text-white opacity-90" />
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="self-start flex items-start gap-3 max-w-full">
      <div className="flex-shrink-0">{/* optional sender avatar could go here */}</div>
      <div>
        <div className="rounded-br-2xl rounded-tr-2xl rounded-tl-xl px-4 py-2 bg-white text-gray-900 shadow-md">
          <div className="text-sm leading-relaxed">{isDecrypting ? 'Decrypting...' : decrypted}</div>
        </div>
        <div className="text-xs text-gray-400 mt-1">{timeLabel}</div>
      </div>
    </div>
  );
};

export default ChatContainer;
